from abc import ABC, abstractmethodclass Vechicle(ABC):    @abstractmethod    def __init__(self):        pass    @abstractmethod    def get_wheels(self):        passclass Car(Vechicle):    def __init__(self):        pass    def get_wheels(self):        return 4class Bike(Vechicle):    def __init__(self):        pass    def get_wheels(self):        return 2class NullObject(Vechicle):    def __init__(self):        pass    def get_wheels(self):        return 0