from LowLevelDesign.ProxyDesingPattern.server.server import DBServerfrom LowLevelDesign.ProxyDesingPattern.server.mysql_server import MySQLServerclass ProxyServer(DBServer):    def __init__(self, user):        self.user = user        self.mysql_server = MySQLServer(self.user)    def create_user(self):        if self.user == "admin":            return self.mysql_server.create_user()        return "Access Denied: create_user."    def delete_user(self):        if self.user == "admin":            return self.mysql_server.delete_user()        return "Access Denied: Delete User"    def fetch_user(self):        return self.mysql_server.fetch_user()