from LowLevelDesign.VendingMachine.Inventory.product_enum import Productfrom LowLevelDesign.VendingMachine.Inventory.inventory import Inventoryfrom LowLevelDesign.VendingMachine.vending_machine.vending_machine import VendingMachine# Create list of productspepsi = Product(name="pepsi", count=10, cost=20)sprite = Product(name="sprite", count=5, cost=15)water = Product(name="water", count=20, cost=10)# Create Inventory & Update it productsproduct_list = [pepsi, sprite, water]inventory_obj = Inventory(product_list)# crate vending machine objectvending_machine = VendingMachine(inventory_obj)# Operationsvending_machine.state.cash_deposit(10)vending_machine.state.select_product()print(vending_machine.state)print(vending_machine.state.state)vending_machine.state.state.select_product()